///<summary>
/// @purpose: Dynamically create table for employee webpage and add functionality to all buttons and controls
/// @date: 2015-10-28
/// @author: Justin Bonello
/// @revisions: 8
///</summary>



$(function () {
    $("#EmployeeModalForm").validate({
        rules: {
            TextboxTitle: { maxlength: 4, required: true, validTitle: true },
            TextboxFirstname: { maxlength: 25, required: true },
            TextboxLastname: { maxlength: 25, required: true },
            TextboxEmail: { maxlength: 40, required: true, email: true },
            TextboxPhone: { maxlength: 15, required: true },
            ddlDepts: { required: true }
        },
        ignore: ".ignore, :hidden",
        errorElement: "div",
        wrapper: "div", //a wrapper around the error message
        messages: {
            TextboxTitle: {
                required: "required 1-4 chars.", maxlength: "required 1-4 chars.", validTitle: "Mr. Ms. Mrs. or Dr."
            },
            TextboxFirstname: {
                required: "required 1-25 chars.", maxlength: "required 1-25 chars."
            },
            TextboxLastname: {
                required: "required 1-25 chars.", maxlength: "required 1-25 chars."
            },
            TextboxPhone: {
                required: "required 1-15 chars.", maxlength: "required 1-15 chars."
            },
            TextboxEmail: {
                required: "required 1-40 chars.", maxlength: "required 1-40 chars.", email: "need valid email format"
            },
            ddlDepts: {
                required: "required department"
            }
        }
    });

    getAll("Employees Loaded");

    $("#main").click(function (e) { //click on any row

        //   reset validation
        var validator = $('#EmployeeModalForm').validate();
        validator.resetForm();

        if (!e) e = window.event;
        var empId = e.target.parentNode.id;

        if (empId == "main") {
            empId = e.target.id;  //clicked on a row somewhere else
        }

        if (empId != "employee") {
            getById(empId);
            $("#ButtonAction").prop("value", "Update");
            $("#ButtonDelete").show();
        }
        else {
            $("#BottonDelete").hide();
            $("#ButtonAction").prop("value", "add");
            $("#HiddenId").val("new");
            $("#TextboxTitle").val("");
            $("#TextboxFirstname").val("");
            $("#TextboxLastname").val("");
            $("#TextboxPhone").val("");
            $("#TextboxEmail").val("");
            $("#ButtonUpdate").prop("value", "Add");
            $("#ButtonDelete").hide("");
            $("#CheckBoxIsTech").prop("checked", false);
            $('#HiddenStaffPicture').val("");
            $('#ImageHolder').html('<img id="StaffPicture" height="120" width="110" src="data:image/png;base64, ' + $('#HiddenStaffPicture').val() + ' " />');
            loadDepartmentDDL(-1);
        }
    })

    //Modal Buttons click - make sure to return false for click or REST calls get cancelled
    $("#ButtonDelete").click(function(){
        var deleteEmp = confirm("really delete this employee?");
        if (deleteEmp) {
            _delete();
            return false;
        }
    });

    $("#ButtonAction").click(function () {
        if ($("#EmployeeModalForm").valid()) {
            var reader = new FileReader();
            var file = $('#fileUpload')[0].files[0];

            reader.onload = function (readerEvt) {
                var binaryString = readerEvt.target.result;
                $('#HiddenStaffPicture').val(btoa(binaryString));
                if ($("#ButtonAction").val() === "Update") {
                    update();
                }
                else {
                    create();
                }
            };

            if (file) {                            // are we adding or updating a pic?
                reader.readAsBinaryString(file);   // yes, read pic file, fire event 
            }
            else {                                 // no pic, regular add or update          
                if ($('#ButtonAction').val() === "Update") {
                    update();
                }
                else {
                    create();
                }
            }
        }
        else {
            $("#lblstatus").text("fix existing problems");
            $("#lblstatus").css({ "color": "red" });
        }
        return false;
    });

    $("#X").click(function () {
        $("#lblstatus").text(null);
    });

    $.validator.addMethod("validTitle", function (value, element) { // custom rule
        return this.optional(element) || (value == "Mr." || value == "Ms." || value == "Mrs." || value == "Dr.");
    }, "");
});

function copyInfoToModal(emp) {
    $("#TextboxTitle").val(emp.Title);
    $("#TextboxFirstname").val(emp.Firstname);
    $("#TextboxLastname").val(emp.Lastname);
    $("#TextboxPhone").val(emp.Phoneno);
    $("#TextboxEmail").val(emp.Email);
    $('#ImageHolder').html('<img id="StaffPicture" height="120" width="110" src="data:image/png;base64, ' + emp.StaffPicture64 + ' " />');
    $("#HiddenId").val(emp.Id);
    $("#HiddenEntity").val(emp.Entity64);
    $("#HiddenStaffPicture").val(emp.StaffPicture64);
    if (emp.IsTech) {
        $("#CheckBoxIsTech").prop("checked", true);
    }
    else {
        $("#CheckBoxIsTech").prop("checked", false);
    }
    loadDepartmentDDL(emp.DepartmentId)
}

//load drop down
function loadDepartmentDDL(empdep) {
    $.ajax({
        type: "Get",
        url: "api/departments",
        contentType: "application/json; charset=utf-8"
    })
    .done(function (data) {
        html = "";
        $("#ddlDepts").empty();
        $.each(data, function () {
            html += "<option value=\"" + this["Id"] + "\">" + this["DepartmentName"] + "</option>";
        });
        if (empdep == -1) {
            html += "<option value=\"\" disabled selected>-- select department --</option>";
            $("#ddlDepts").append(html);
        }
        else {
            $("#ddlDepts").append(html);
            $("#ddlDepts").val(empdep);          
        }
    })
    .fail(function (jqXHR, textStatus, errorThrown) {
        alert("error");
    });
}

//build initial table
function buildTable(data) {
    $("#main").empty();
    var bg = false;
    employees = data; // copy to global var
    div = $("<div id=\"employee\" data-toggle=\"modal\" data-target=\"#myModal\" class=\"row trWhite\">");
    div.html("<div class=\"col-lg-12 rowBlue\" style=\"text-align: center;\" id=\"id0\">...Click Here to add</div>");
    div.appendTo($("#main"));

    $.each(employees, function (index, emp) {
        var cls = "rowBlue";
        bg ? cls = "rowBlue" : cls = "rowLightBlue";
        bg = !bg;
        div = $("<div id=\"" + emp.Id + "\" data-toggle=\"modal\" data-target=\"#myModal\"  style=\"padding:0 0 0 0; width:110%;\"class=\"row col-lg-12 " + cls + "\">");
        var empId = emp.Id;
        div.html(
                        "<div class=\"col-xs-4\" style=\"text-indent: 5em;\" id=\"employeetitle" + empId + "\">" + emp.Title + "</div>" +
                        "<div class=\"col-xs-4\" id=\"employeefname" + empId + "\">" + emp.Firstname + "</div>" +
                        "<div class=\"col-xs-4\" id=\"emplastname" + empId + "\">" + emp.Lastname + "</div>" +
                        "</div>"
                        );
        div.appendTo($("#main"));
    }); //each
} //build table

function ajaxCall(type, url, data) {
    return $.ajax({ //return the promise that '$.ajax' returns
        type: type,
        url: url,
        data: JSON.stringify(data),
        contentType: "application/json; charset=utf-8",
        dataType: "json",
        processData: true
    });
}

function getAll(msg) {
    $("LabelStatus").text("Loading...");

    ajaxCall("Get", "api/employees", "")
    .done(function (data) {
        buildTable(data);
        if (msg == "")
            $("#LabelStatus").text("Employees Loaded");
        else
            $("#LabelStatus").text(msg);
    })
    .fail(function (jqXHR, textStatus, errorThrown) {
        errorRoutine(jqXHR);
    });
}

function getById(empId) {
    ajaxCall("Get", "api/employees/" + empId, "")
    .done(function (data) {
        copyInfoToModal(data);
    })
    .fail(function (jqXHR, textStatus, errorThrown) {
        errorRoutine(jqXHR);
    });
}

function create() {
        emp = new Object();
        emp.Title = $("#TextboxTitle").val();
        emp.Firstname = $("#TextboxFirstname").val();
        emp.Lastname = $("#TextboxLastname").val();
        emp.Phoneno = $("#TextboxPhone").val();
        emp.Email = $("#TextboxEmail").val();
        emp.DepartmentId = $("#ddlDepts").val();
        emp.StaffPicture64 = $("#HiddenStaffPicture").val();
        emp.IsTech = $("#CheckBoxIsTech").is(":checked");

        ajaxCall("Post", "api/employees/", emp)
        .done(function (data) {
            getAll("Employee " + emp.Lastname + " added");
            $("#myModal").modal("toggle");
        })
        .fail(function (jqXHR, textStatus, errorThrown) {
            errorRoutine(jqXHR);
        });
}

function update() {
        emp = new Object();
        emp.Title = $("#TextboxTitle").val();
        emp.Firstname = $("#TextboxFirstname").val();
        emp.Lastname = $("#TextboxLastname").val();
        emp.Phoneno = $("#TextboxPhone").val();
        emp.Email = $("#TextboxEmail").val();
        emp.DepartmentId = $("#ddlDepts").val();
        emp.Id = $("#HiddenId").val();
        emp.StaffPicture64 = $("#HiddenStaffPicture").val();
        emp.Entity64 = $("#HiddenEntity").val();
        emp.IsTech = $("#CheckBoxIsTech").is(":checked");

        ajaxCall("Put", "api/employees/", emp)
        .done(function (data) {
            getAll("");
            copyInfoToModal(emp);
            $("#lblstatus").text(data);
        })
        .fail(function (jqXHR, textStatus, errorThrown) {
            errorRoutine(jqXHR);
        });

}

function errorRoutine(jqXHR) { //common error routine
    if (jqXHR.responseJSON == null) {
        $("#lblstatus").text(jqXHR.responseText);
    }
    else {
        $("#lblstatus").text(jqXHR.responseJSON.Message);
    }
} 

function _delete() {
    ajaxCall("Delete", "api/employees/" + $("#HiddenId").val(), "")
    .done(function (data) {
        getAll(data);
        $("#myModal").modal("toggle");
    })
    .fail(function (jqXHR, textStatus, errorThrown) {
        $("#myModal").modal("toggle");
        errorRoutine(jqXHR)
    });
}